import * as R from 'ramda'

import clothes from 'api/mockClothes'
import categories from 'api/mockCategories'

export const fetchClothes = async () => {

  return new Promise(resolve => {

    resolve(clothes)

  })

}

export const loadMoreClothes = async ({offset}) => {
  return new Promise(resolve => {
    resolve(clothes)
  })
}

export const fetchItemById = async (id) => {
  return new Promise(resolve => {
    const item = R.find(R.propEq('id', id), clothes)
    resolve(item)
  })
}

export const fetchCategories = async () => {
  return new Promise(resolve => {
    resolve(categories)
  })
}
