import React from 'react'
import BasketCart from 'components/basketCart'
import styles from './navbar.module.css'
import {Link} from 'react-router-dom'

const Navbar = (props) =>{

  const openNav = () => {

    if (document.getElementById("myNav") && document.getElementById("sidebar")) {

      document.getElementById("myNav").style.width = "100%"
      document.getElementById("sidebar").style.display = "block"

    }

  }

  if (props.name === '/') {
    return (

      <nav className={styles.navbar}>
      <span onClick={openNav}>
        <img src="/images/iconfinder_menu-alt_134216.svg" alt=""/>
      </span>
        <Link to="/" className={styles.logo}>CLOTHES.</Link>
        <BasketCart/>
      </nav>

    )
  } else {
    return (

      <nav className={styles.navbar}>
      <Link to='/'>
        <img src="/images/iconfinder_icon-ios7-arrow-back_211686.svg" alt=""/>
      </Link>
        <Link to="/" className={styles.logo}>CLOTHES.</Link>
        <BasketCart/>
      </nav>

    )
  }

}

export default Navbar